import React from "react";
import { Carousel } from "react-bootstrap";
import SliderItem from "./SliderItem";
import bgImages from "../../resouces/images/backgroundImages/bgImages";

function SliderImages() {
    /*
    {
        bgImages.map((item) => {
            return  (
                 <SliderItem imageSrc={item.src} imageAlt={item.alt} imageTitle={item.title} imageContent={item.content} />
             );
         })
     }
     */
    return (
        <Carousel>
            {
                bgImages.map((item) => {
                    return (
                        <Carousel.Item>
                            <img
                                className="d-block w-100"
                                src={item.src} 
                                alt={item.alt}
                            />
                            <Carousel.Caption>
                                <h3>{item.title}</h3>
                                <p>{item.content}</p>
                            </Carousel.Caption>
                        </Carousel.Item>


                    );
                })
            }
            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src={bgImages[0].src}
                    alt="First slide"
                />
                <Carousel.Caption>
                    <h3>First slide label</h3>
                    <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                </Carousel.Caption>
            </Carousel.Item>

            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src={bgImages[1].src}
                    alt="Third slide"
                />

                <Carousel.Caption>
                    <h3>Second slide label</h3>
                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src={bgImages[2].src}
                    alt="Third slide"
                />

                <Carousel.Caption>
                    <h3>Third slide label</h3>
                    <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
                </Carousel.Caption>
            </Carousel.Item>
        </Carousel>
    );
}

export default SliderImages;
