import React from "react";
import bgImages from "../../resouces/images/backgroundImages/bgImages"
//import bg from "/resouces/images/backgroundImages/bg_1.jpg";
//import bgImage from "./bg_1.jpg";
console.log(bgImages);
//console.log(bg);
function SliderImages(){

    return (
    <section className="home-slider owl-carousel" style={{display: "block"}}>
      <div className="slider-item" style={{backgroundImage: `url(${bgImages[0]})`}}>
      	<div className="overlay"></div>
        <div className="container">
          <div className="row slider-text justify-content-center align-items-center" data-scrollax-parent="true">

            <div className="col-md-8 col-sm-12 text-center ftco-animate">
            	<span className="subheading">Welcome</span>
              <h1 className="mb-4">The Best Coffee Testing Experience</h1>
              <p className="mb-4 mb-md-5">A small river named Duden flows by their place and supplies it with the necessary regelialia.</p>
              <p><button className="btn btn-primary p-3 px-xl-4 py-xl-3">Order Now</button> <button className="btn btn-white btn-outline-white p-3 px-xl-4 py-xl-3">View Menu</button></p>
            </div>

          </div>
        </div>
      </div>


    </section>
    );
}

export default SliderImages;
