import React from "react";
import { Carousel } from "react-bootstrap";
import bgImages from "../../resouces/images/backgroundImages/bgImages";
import "./style.css"

function SliderImages() {
    /*
    {
        bgImages.map((item) => {
            return  (
                 <SliderItem imageSrc={item.src} imageAlt={item.alt} imageTitle={item.title} imageContent={item.content} />
             );
         })
     }
     */
    return (
        <Carousel>
            {
                bgImages.map((item) => {
                    return (
                        <Carousel.Item>
                            <img
                                className="d-block w-100"
                                src={item.src} 
                                alt={item.alt}
                            />
                            <Carousel.Caption className="caption-position">
                                <span className="subheading">Welcome</span>
                                <row>
                                    <Col xs={6} md={4}>
                                        <h1>{item.title}</h1>
                                    </Col>
                                </row>

                                <p>{item.content}</p>
                            </Carousel.Caption>
                        </Carousel.Item>
                    );
                })
            }
        </Carousel>
    );
}

export default SliderImages;
