import React from "react";
import { Carousel, Row, Col, Button } from "react-bootstrap";
import bgImages from "../../resouces/images/backgroundImages/bgImages";
import "./style.css"

function SliderImages() {
    /*
    {
        bgImages.map((item) => {
            return  (
                 <SliderItem imageSrc={item.src} imageAlt={item.alt} imageTitle={item.title} imageContent={item.content} />
             );
         })
     }
     */
    return (
        <Carousel>
            {
                bgImages.map((item) => {
                    return (
                        <Carousel.Item>
                            <img
                                className="d-block w-100"
                                src={item.src} 
                                alt={item.alt}
                            />
                            <Carousel.Caption className="caption-pos">
                                <span className="subheading">Welcome</span>
                                <Row>
                                    <Col md={{span: 6, offset: 3}}>
                                        <h1>{item.title}</h1>
                                    </Col>
                                </Row>

                                <p>{item.content}</p>
                                <Button bg="transparent" variant="transparent">Order Now</Button>
                                <Button variant="transparent">View Menu</Button>
                            </Carousel.Caption>
                        </Carousel.Item>
                    );
                })
            }
        </Carousel>
    );
}

export default SliderImages;
